import React from 'react';
import { Link } from "react-router-dom";
import styled from "styled-components";
import { mobile } from "../responsive";


const Container = styled.div`
  height: 60px;
  ${mobile({ height: "50px" })}
`;

const Wrapper = styled.div`
  padding: 22px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  ${mobile({ padding: "10px 0px" })}
`;

const Section = styled.div`
    flex: 1;
    // display: flex;
    align-items: center;
    // flex-direction: column;
    :hover {
        border-bottom: 2px solid teal;
    }

`;

const SectionCenter = styled.div`
    display: flex;
    flex: 1;
  text-align: center;
`;

const link = {
    textDecoration: "none",
    color: "inherit"
}


const CategorySection = () => {
    return (
        <Container>
            <Wrapper>
                <SectionCenter>
                    <Section>
                        <Link to="/men-collection" style={link}>
                            Men
                        </Link>
                    </Section>
                    <Section>
                        <Link to="/women-collection" style={link}>
                            Women
                        </Link>
                    </Section>
                    <Section>
                        <Link to="/kids-collection" style={link}>
                            Kids
                        </Link>
                    </Section>
                </SectionCenter>
            </Wrapper>
        </Container>
    )
}

export default CategorySection
